import classes from './MealItemForm.module.css';

const MealItemFrorm = props => {

    return <form  className={classes.form}>

        <Input  label="Amount" input={{

id:'amount',
type:'number',
min : '1',
max :'5',
step:'1',
defaultValue :'1'



        }}/>
        <button>+ Add</button>
    </form>
};

export default MealItemFrorm;
